mapper.update:
UPDATE Maintenance
SET 
	MT_CONST = #{mtConst},
	MT_LOCATION = #{mtLocation},
	MT_DETAIL = #{mtDetail}

	,ATTACH = #{attach}

WHERE MT_SEQ = #{mtSeq}

mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY A.MT_SEQ ASC) RNUM, A.MT_SEQ, A.MEM_ID, A.MT_CONST, A.MT_LOCATION, TO_CHAR(A.REG_DT, 'YYYY/MM/DD') REG_DT, A.MT_STATUS, TO_CHAR(A.PRCS_DT, 'YYYY/MM/DD') PRCS_DT, A.MT_NOTE, (SELECT FILE_NAME FROM ATTACH B WHERE A.ATTACH = B.GLOBAL_CODE AND ROWNUM = 1) AS FILE_NAME FROM MAINTENANCE A WHERE 1=1
AND MT_STATUS = #{schMtStatus}
ORDER BY RNUM DESC

mapper.update:
UPDATE MAINTENANCE
SET
	CCPY_CODE = #{ccpyCode}
WHERE MT_SEQ = #{mtSeq}
